import React, { useState, useEffect } from 'react';
import { Component } from 'react';
import { DataTable } from 'primereact/datatable';
import { Dropdown } from 'primereact/dropdown'
import { Column } from 'primereact/column';
import { ProductService } from './service/ProductService';
import { MunicipioZonaDataService } from './service/municipio_zona.service';

export default class App extends Component {
    constructor(props) {
        super();
        this.retrieveMunicipios = this.retrieveMunicipios.bind(this);
        this.retrieveZonas = this.retrieveZonas.bind(this);
        this.retrieveMagistrados = this.retrieveMagistrados.bind(this);
        this.state = {
            municipios: [],
            zonas: [],
            magistrados: []
        };
    }
    componentDidMount() {
        this.retrieveZonas();
        this.retrieveMunicipios();
        //this.updateZonas();
    }
    updateZonas() {
        let items = this.state.zonas;
        console.log(items.length);
        items.forEach((zona, index) => {
            zona.LABEL = zona.NUMERO + " - " + zona.SEDE;
          });

    }
    retrieveZonas() {
        MunicipioZonaDataService.getAllZonas()
            .then(response => {
                this.setState({
                    zonas: response.data
                }, () => {  this.updateZonas();});
            })
            .catch(e => {
                console.log(e);
            });
    }
    retrieveMunicipios() {
        MunicipioZonaDataService.getAllMunicipios()
            .then(response => {
                this.setState({
                    municipios: response.data
                });
                //console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    }

    retrieveMagistrados(sede) {
        MunicipioZonaDataService.getAllMagistrados(sede)
            .then(response => {
                this.setState({
                    magistrados: response.data
                }, () => { });
            })
            .catch(e => {
                console.log(e);
            });
    }
    setMunicipio(valor) {
        //alert(this.state.selectedMunicipio);
        //alert(valor);
        this.setState({selectedSedeZona: valor});
        this.retrieveMagistrados(valor);
    }
    render() {
        return (
            <div className="card flex justify-content-center">
                <Dropdown options={this.state.zonas} 
                    optionValue="SEDE" optionLabel="LABEL"
                    onChange={(e) => this.setMunicipio(e.value)} 
                    placeholder="Escolha uma zona" className="w-full md:w-14rem" 
                    value={this.state.selectedSedeZona} filter />
                <div className="card">
                    <DataTable value={this.state.magistrados} tableStyle={{ minWidth: '50rem' }} rows={5} paginator>
                        <Column field="nome" sortable header="Nome"></Column>
                        <Column field="comarca" sortable header="Comarca"></Column>
                        <Column field="distancia" sortable header="DistÃ¢ncia"></Column>
                    </DataTable>
                </div>
            </div>
        );
    }


}

