import React, { useState, useEffect } from 'react';
import { Component } from 'react';
import { DataTable } from 'primereact/datatable';
import { Dropdown } from 'primereact/dropdown'
import { Column } from 'primereact/column';
import { ProductService } from './service/ProductService';
import { MunicipioZonaDataService } from './service/municipio_zona.service';

export default class App extends Component {
    constructor(props) {
        super();
        this.retrieveMunicipios = this.retrieveMunicipios.bind(this);
        this.retrieveZonas = this.retrieveZonas.bind(this);
        this.state = {
            municipios: [],
            zonas: []
        };
    }
    componentDidMount() {
        this.retrieveZonas();
        this.retrieveMunicipios();
        //this.updateZonas();
    }
    updateZonas() {
        /*let items = this.state.zonas;
        console.log(items.length);
        items.forEach((zona, index) => {
            zona.TESTE = zona.NUMERO + " - " + zona.SEDE;
            console.log(zona.LABEL);
          });
        this.setState({
            zonas: items,
        });*/

    }
    retrieveZonas() {
        MunicipioZonaDataService.getAllZonas()
            .then(response => {
                this.setState({
                    zonas: response.data
                }, () => {  this.updateZonas});
                /*this.state.zonas.forEach((zona, index) => {
                  this.state.zonas[index].LABEL = zona.NUMERO + " - " + zona.SEDE;
                  this.forceUpdate();
                  console.log(zona.LABEL);
                });*/
                /*this.setState({
                  zonas: zt
                });*/
                //console.log(response.data);
            })
            .then(d => {
                this.updateZonas();
            })
            .catch(e => {
                console.log(e);
            });
        /*this.state.zonas.forEach((zona, index) => {
            zona.LABEL = zona.NUMERO + " - " + zona.SEDE;
            console.log(zona.LABEL);
        });*/
    }
    retrieveMunicipios() {
        MunicipioZonaDataService.getAllMunicipios()
            .then(response => {
                this.setState({
                    municipios: response.data
                });
                //console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });

    }
    render() {
        return (
            <div className="card flex justify-content-center">
                <Dropdown options={this.state.zonas} optionLabel="LABEL"
                    placeholder="Escolha uma zona" className="w-full md:w-14rem" filter />
                <div className="card">
                    <DataTable value={this.state.municipios} tableStyle={{ minWidth: '50rem' }} rows={5} paginator>
                        <Column field="ID" sortable header="Code"></Column>
                        <Column field="NOME" sortable header="Name"></Column>
                    </DataTable>
                </div>
            </div>
        );
    }


}

